import React, { useState } from 'react';
import { useHistory, useParams } from 'react-router-dom';
import { Col, Container, Row, Button, Tab, Tabs, Sonnet } from 'react-bootstrap';

function Detail(props){
  let {id} = useParams();
  let history = useHistory();
  let detailProduct = props.productData.find(function(product){
    return(product.id == id)
  });

    return (
      <div className="container">
        <div className="row">
          <div className="col-md-6">
            <img src={'/img/' + (id) + '.jpg'} width="100%" />
          </div>
          <div className="col-md-6 mt-4">
            <h4 className="pt-5">{detailProduct.name}</h4>
            <p>{detailProduct.description}</p>
            <p>{detailProduct.cost}</p>
            <button className="btn btn-danger">주문하기</button> 
            &nbsp;
            <button className="btn btn-danger" onClick={ ()=>{ history.push('/') } }>뒤로</button>
          </div>
        </div>

        <Tabs defaultActiveKey="profile" id="uncontrolled-tab-example" className="mb-3">
          <Tab eventKey="home" title="Home">
            <SignUp />
          </Tab>
          <Tab eventKey="profile" title="Profile">
            <SignUp />
          </Tab>
          <Tab eventKey="contact" title="Contact" disabled>
            <SignUp />
          </Tab>
        </Tabs>

      </div>  
    )
  };


  export default Detail;
