import React, { useState } from 'react';
import { ListGroup, Button, Container, Row, Col } from 'react-bootstrap';
import './App.css';
import { Link, useHistory } from 'react-router-dom';

function Cart(props) {
    let history = useHistory();
    let product = useState(['']);

    return(
        <div>
            <Table striped bordered hover>
                <thead>
                    <tr>
                        <th>#</th>
                        <th>상품명</th>
                        <th>수량</th>
                        <th>증가</th>
                        <th>감소</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        props.state.map((a,i)=>{
                            return (
                                <tr key={i}>
                                    <td>{ a.id }</td>
                                    <td>{ a.name }</td>
                                    <td>{ a.quan }</td>
                                    <td><button onClick={ ()=>{ props.dispatch({ type : 'add', incomeData : a.id }) } }>+</button></td>
                                    <td><button onClick={ ()=>{ props.dispatch({ type : 'minus', incomeData : a.id }) } }>-</button></td>
                                </tr>
                            )
                        })
                    }
                </tbody>
            </Table>

            { props.alertState === true
            ? (<div className="saleAlert">
                <p>첫 구매시 20% 할인</p>
                <button onClick={()=>{ props.dispatch({ type : 'close' }) }}>닫기</button>
              </div>)
            : null
            }     
        </div>
        
    )
}

function 함수명(state) {
    return {
        state : state.reducer,
        alertState : state.reducer2
    }
}

export default connect(함수명)(Cart)
