import React, { useState } from 'react';
import { Form, Button } from 'react-bootstrap';
import axios from 'axios';
import { useHistory, useParams } from 'react-router-dom';



function QnaDetail(props) {
  let {id} = useParams();
  let [title, changeTitle] = useState('');
  let [content, changeContent] = useState('');
  let history = useHistory();
  let detailProduct = props.productData.find(function(product){
    return(product.id == id)
  });


  return (
    <div>
      {props.qnaData[id-1].title}
      <h4 className='specificQnaInput'>
        
      <Button onClick={()=>{ 
        axios.post('/user', {
          title: title,
          content: content
        })
        .then(function (response) {
          console.log(title);
          console.log(content);
        })
        .catch(function (error) {
          console.log(title);
          console.log(content);
        });
      }}>전송</Button>
      &nbsp;
      <Button onClick={ ()=>{ history.goBack(); } }>뒤로 가기</Button>
    </h4>
  </div>
  )
}








export default QnaDetail
